  for each row
declare
cursor c_get_dossier ( b_dossier_seq number ) is
   select 1
     from export_dossiers ,
          delivery_terms ,
          relations
    where export_dossiers.export_dossier_seq = b_dossier_seq
      and export_dossiers.delivery_term_seq = delivery_terms.delivery_term_seq
      and export_dossiers.relation_seq = relations.relation_seq
      and delivery_terms.delivery_term = 'EXW'
      and relations.agent_flag = 'Y'
      and export_dossiers.self_delivery = 'E'
      and ( export_dossiers.EXCLUDE_MONITORING_FLAG = 'N' or export_dossiers.EXCLUDE_MONITORING_FLAG is null ) ;
r_get_dossier c_get_dossier%rowtype;

begin
  if :NEW."DOSIER_STATUS_SEQ" is null then
    select "DOSSIER_STATUSES_SEQ".nextval 
      into :NEW."DOSIER_STATUS_SEQ" 
      from dual;
     :NEW.CREATE_DATE := sysdate;
     
     if :new.status = 'Arrival Destination Depot' then
     open c_get_dossier ( :NEW.EXPORT_DOSSIER_SEQ);
     fetch c_get_dossier into r_get_dossier;
     if c_get_dossier%found then
     
      update export_dossiers
         set EXCLUDE_MONITORING_FLAG = 'Y'
       where export_dossier_seq  =  :NEW.EXPORT_DOSSIER_SEQ; 
     
     end if;
     close c_get_dossier;
     
     
     end if;
     
  end if;

  update SHIPMENT_CHANGES
        set transferred_flag = 'N' ,
              transferred_date = null,
              update_date = sysdate
