--  DDL for Trigger BI_EXPORT_DOSSIERS
--------------------------------------------------------

  CREATE OR REPLACE EDITIONABLE TRIGGER "TRANSPORT"."BI_EXPORT_DOSSIERS" 
  before insert or update on "EXPORT_DOSSIERS"
  for each row
    
declare


cursor c_get_relation ( b_relation_seq number ) is
       select 1
         from relations
        where relation_seq = b_relation_seq
          and agent_flag = 'Y';

r_get_relation c_get_relation%rowtype;


cursor c_get_delivery_term (b_delivery_term_seq number ) is
       select 1
         from delivery_terms
        where delivery_term_seq = b_delivery_term_seq
          and delivery_term = 'EXW';
r_get_delivery_term c_get_delivery_term%rowtype;

cursor c_get_addresses ( b_address_seq number ) is
    select *
      from addresses
     where address_seq = b_address_seq;

r_get_addresses c_get_addresses%rowtype;
    
cursor c_get_status is
    select status.status_seq
      from status
     where status.code = 'CANCELED';
r_get_status c_get_status%rowtype;
    
cursor c_get_dossier_status (b_export_dossier_seq number) is
  select *
    from DOSSIER_STATUSES 
 where export_dossier_seq = b_export_dossier_seq
  order by dosier_status_seq desc;
r_get_dossier_status c_get_dossier_status%rowtype;

cursor c_get_relation_status ( b_relation_seq number ) is
     select 1
       from relation_statuses
      where relation_seq = b_relation_seq
          and active_flag = 'Y'
           and status_code = 'Schedule Change';
r_get_relation_status c_get_relation_status%rowtype;

begin


-- CANCELED !!
open c_get_status;
fetch c_get_status into r_get_status;
close c_get_status;


if r_get_status.status_seq = :new.status_seq then
  :new.EXCLUDE_MONITORING_FLAG := 'Y';

  else

if :new.self_delivery = 'E' then
 open c_get_delivery_term (:new.delivery_term_seq);
 fetch c_get_delivery_term into r_get_delivery_term;
 if c_get_delivery_term%found then
 
   open c_get_relation (:new.relation_seq);
   fetch c_get_relation into r_get_relation;
   if c_get_relation%found then
   
      null;
   
      --:new.EXCLUDE_MONITORING_FLAG := 'Y';
   
   end if;
   close c_get_relation;

 end if;
 close c_get_delivery_term;

end if;

end if;

if :new.consol_flag = 'Y' then
  :new.status_seq := 101;  
end if; 

if inserting then
  if :NEW."EXPORT_DOSSIER_SEQ" is null then
    select "EXPORT_DOSSIERS_SEQ".nextval 
      into :NEW."EXPORT_DOSSIER_SEQ" 
      from dual;

  :NEW.OFFICE_SEQ := 2;
  :NEW.SHOW_POD_FLAG := 'Y';

  if :new.dossier_number is null then

  SELECT lpad ( ACTUAL_SEQUENCE + 1 , 8 , 0 )
    into :new.dossier_number
    FROM ENTITY_SEQUENCES
   WHERE ENTITY_NAME = 'DOSSIER'
     AND ENTITY_TYPE = 'E'
     AND OFFICE_SEQ  = :NEW.OFFICE_SEQ
   for update nowait;

  UPDATE ENTITY_SEQUENCES
     SET ACTUAL_SEQUENCE = :new.dossier_number
   WHERE ENTITY_NAME = 'DOSSIER'
     AND ENTITY_TYPE = 'E'
     AND OFFICE_SEQ  = :NEW.OFFICE_SEQ;

  end if;

  :new.sales_fixed_d_flag := :new.fixed_d_flag;


  if :new.unload_relation_seq is not null then
    open c_get_addresses (:new.unload_relation_seq);
    fetch c_get_addresses into r_get_addresses; 
    if c_get_addresses%found then
        :new.sales_box_flag := r_get_addresses.box_sales_flag;
        :new.sales_port_fee_flag := r_get_addresses.port_fee_sales_flag;
