--------------------------------------------------------

  CREATE OR REPLACE EDITIONABLE TRIGGER "TRANSPORT"."BI_EXPORT_INVOICES" 
  before insert or update on "EXPORT_INVOICES"
  for each row
declare
cursor c_get_office is
   select YEAR_FINANCE_CLOSED_UPTO,
             month_finance_closed_upto
     from offices
    where office_seq = v('USER_DEF_OFFICE_SEQ');
  r_get_office c_get_office%rowtype;

cursor c_get_export_dossier (b_export_dossier_seq number) is
  select l_date
   from export_dossiers
 where export_dossier_seq = b_export_dossier_seq;
r_get_export_dossier  c_get_export_dossier%rowtype;

begin
 open c_get_office;
 fetch c_get_office into r_get_office;
 close c_get_office;

 open c_get_export_dossier (:NEW.EXPORT_DOSSIER_SEQ);
 fetch c_get_export_dossier into r_get_export_dossier;
 close c_get_export_dossier;
 
 if ( to_number(to_char(r_get_export_dossier.l_date,'YYYYMM')) <= r_get_office.YEAR_FINANCE_CLOSED_UPTO||nvl(r_get_office.month_finance_closed_upto,'01') ) and r_get_office.YEAR_FINANCE_CLOSED_UPTO  is not null then

   raise_application_error ( -20010 , 'Financial year and period closed upto '||r_get_office.YEAR_FINANCE_CLOSED_UPTO||nvl(r_get_office.month_finance_closed_upto,'01')||', you are not allowed to book in this period !!!');

 end if; 
  

  if :NEW."EXPORT_INVOICE_SEQ" is null then
    select "EXPORT_INVOICES_SEQ".nextval into :NEW."EXPORT_INVOICE_SEQ" from dual;
    :new.create_date := sysdate;
    :new.create_user := v('APP_USER');

  update SHIPMENT_CHANGES
        set transferred_flag = 'N' ,
              transferred_date = null,
              update_date = sysdate
   where booking_seq = :new.export_dossier_seq;

  end if;

  select ( :new.amount  / 100 ) * percentage
    into :new.vat_amount
    from vats
  where vat_seq = :new.vat_seq;

  if :NEW.OUTGOING_INVOICE_SEQ is null then
     update export_dossiers
        set financial_status = 3
      where export_dossier_seq = :NEW.EXPORT_DOSSIER_SEQ; 
  end if;

  if :NEW.OUTGOING_INVOICE_SEQ is not null then
     update export_dossiers
        set financial_status = 2
      where export_dossier_seq = :NEW.EXPORT_DOSSIER_SEQ; 
  end if;
  
  if updating then
    :new.update_date := sysdate;
    :new.updated_by := v('APP_USER');

  update SHIPMENT_CHANGES
        set transferred_flag = 'N' ,
              transferred_date = null,
              update_date = sysdate
   where booking_seq = :new.export_dossier_seq;

  end if;

end;

