--  DDL for Trigger REPLACE_MULTIBYTES_BR_TRG
--------------------------------------------------------

  CREATE OR REPLACE EDITIONABLE TRIGGER "TRANSPORT"."REPLACE_MULTIBYTES_BR_TRG" 
    BEFORE INSERT OR UPDATE OF 
      multibyte_ascii
    , multibyte_char
    , single_byte_char
    , single_byte_ascii
    ON replace_multibytes
FOR EACH ROW
BEGIN
  if inserting 
    then 
         if    :new.multibyte_ascii is null  and  :new.multibyte_char is not null 
           then :new.multibyte_ascii := ascii ( :new.multibyte_char );
         elsif :new.multibyte_ascii is not null  and  :new.multibyte_char is null 
           then :new.multibyte_char := chr ( :new.multibyte_ascii );
         end if;
         if    :new.single_byte_ascii is null  and  :new.single_byte_char is not null 
           then :new.single_byte_ascii := ascii ( :new.single_byte_char );
         elsif :new.single_byte_ascii is not null  and  :new.single_byte_char is null 
           then :new.single_byte_char := chr ( :new.single_byte_ascii );
         end if;
  end if;
  if updating 
    then    
         if :new.multibyte_ascii is not null and :old.multibyte_ascii != :new.multibyte_ascii
           then  :new.multibyte_char := chr ( :new.multibyte_ascii );
         elsif :new.multibyte_char is not null and :old.multibyte_char != :new.multibyte_char
           then :new.multibyte_ascii := ascii ( :new.multibyte_char );
         end if;
         if :new.single_byte_ascii is not null and :old.single_byte_ascii != :new.single_byte_ascii
           then  :new.single_byte_char := chr ( :new.single_byte_ascii );
         elsif :new.single_byte_char is not null and :old.single_byte_char != :new.single_byte_char
           then :new.single_byte_ascii := ascii ( :new.single_byte_char );
         end if;
  end if;

